#' Sort
#'
#' Sort Data Set
#'
#' @examples
#' \donttest{
#' # the function is a wrapper for jmvReadWrite::sort_omv
#' # please use that function when in R (or in Rj)
#' # for more information: https://sjentsch.github.io/jmvReadWrite
#'}
#' @param data .
#' @param varSrt .
#' @param varAll .
#' @param ordSrt .
#' @param shwHlp .
#' @param btnCrt .
#' @param jxfLog .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$genInf} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$dtaInf} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$pvwDta} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$pvwDta$asDF}
#'
#' \code{as.data.frame(results$pvwDta)}
#'
#' @export
jtSort <- function(
    data,
    varSrt = NULL,
    varAll = NULL,
    ordSrt = NULL,
    shwHlp = FALSE,
    btnCrt = FALSE,
    jxfLog = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly = TRUE))
        stop("jtSort requires jmvcore to be installed (restart may be required)")

    if ( ! missing(varSrt)) varSrt <- jmvcore::resolveQuo(jmvcore::enquo(varSrt))
    if ( ! missing(varAll)) varAll <- jmvcore::resolveQuo(jmvcore::enquo(varAll))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(varSrt), varSrt, NULL),
            `if`( ! missing(varAll), varAll, NULL))


    options <- jtSortOptions$new(
        varSrt = varSrt,
        varAll = varAll,
        ordSrt = ordSrt,
        shwHlp = shwHlp,
        btnCrt = btnCrt,
        jxfLog = jxfLog)

    analysis <- jtSortClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

